function getTimeDifference(time1, time2) {
  const timeParts1 = time1.split(':').map(Number);
  const timeParts2 = time2.split(':').map(Number);

  const date1 = new Date(2023, 6, 1, timeParts1[0], timeParts1[1], timeParts1[2]).getTime();
  const date2 = new Date(2023, 6, 1, timeParts2[0], timeParts2[1], timeParts2[2]).getTime();

  return Math.abs(date2 - date1);
}


function getDateDifference(date1, date2) {
  const dateParts1 = date1.split('/').map(Number);
  const dateParts2 = date2.split('/').map(Number);

  const d1 = new Date(dateParts1[2], dateParts1[1] - 1, dateParts1[0]);
  const d2 = new Date(dateParts2[2], dateParts2[1] - 1, dateParts2[0]);

  const difference = Math.abs(d2.getTime() - d1.getTime());
  const differenceInDays = Math.ceil(difference / (1000 * 3600 * 24));

  return differenceInDays;
}

