function processData(currentActivity, userData) {

    const activity = currentActivity[0].activity;
    const velocities = userData.Velocity || 0;
    const weight = userData.Weight || 0;
    const height = userData.Height || 0;

    const velocity = (activity !== -1) ? (activity === 0 ? velocities : velocities * 2) : 0;

    const calculateCalories = () => {
        const velocitySquared = velocity * velocity;
        return 0.035 * weight + (velocitySquared / height) * 0.029 * weight;
    };

    let totalCaloriesBurned = calculateCalories();

    currentActivity.sort((a, b) => {
        const getDate = (entry) => {
            const [day, month, year] = entry.date.split('/').map(Number);
            const [hours, minutes, seconds] = entry.time.split(':').map(Number);
            return new Date(year, month - 1, day, hours, minutes, seconds).getTime();
        };
        return getDate(a) - getDate(b);
    });

    let totalDistance = 0;
    let totalDuration = 0;
    let timestamp;

    if (currentActivity.length > 1) {
        const firstActivity = currentActivity[0];
        const lastActivity = currentActivity[currentActivity.length - 1];

        const getTimestamp = (entry) => formatDate(entry.date) + 'T' + entry.time;
        const time1 = extractTime(getTimestamp(firstActivity));
        const time2 = extractTime(getTimestamp(lastActivity));

        const durationSeconds = Math.abs(time1 - time2);
        totalDuration = durationSeconds;
        totalDistance = velocity * durationSeconds;
        timestamp = getTimestamp(firstActivity);
    } else if (currentActivity.length === 1) {
        const firstActivity = currentActivity[0];
        timestamp = formatDate(firstActivity.date) + 'T' + firstActivity.time;
    }

    const formattedCalories = totalCaloriesBurned.toFixed(2);
    const formattedDistance = totalDistance.toFixed(2);
    const formattedDuration = formatDuration(totalDuration);
    const formattedDate = formatDateForDB(timestamp);

    const calculatedData = {
        Date: formattedDate,
        ActivityType: activity,
        Distance: formattedDistance,
        Duration: formattedDuration,
        CaloriesBurned: formattedCalories
    };
    if (calculatedData.Duration !== formatDuration(0)){

        return calculatedData;        
    }

}

function formatDate(date) {
    const [day, month, year] = date.split("/").map(Number);
    return `${year}-${String(month).padStart(2, '0')}-${String(day).padStart(2, '0')}`;
}

function extractTime(timestamp) {
    const [_, time] = timestamp.split("T");
    const [hours, minutes, seconds] = time.split(":").map(Number);
    return hours * 3600 + minutes * 60 + seconds;
}

function formatDateForDB(date) {
    return date.slice(0, 8); // Extracts the YYYY-MM-DD part
}

function formatDuration(durationInSeconds) {
    const hours = Math.floor(durationInSeconds / 3600);
    const minutes = Math.floor((durationInSeconds % 3600) / 60);
    const seconds = durationInSeconds % 60;
    return `${String(hours).padStart(2, '0')}:${String(minutes).padStart(2, '0')}:${String(seconds).padStart(2, '0')}`;
}

